import React, { useState } from 'react';
import axios from 'axios';

const statesAndCities = {
  NC: ["Winston", "Charlotte"],
  CA: ["LosAngeles", "SanFrancisco"],
  // Add other states and cities here
};

function App() {
  const [selectedState, setSelectedState] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [monthsAhead, setMonthsAhead] = useState(1);
  const [results, setResults] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/api/predict/', {
        state: selectedState,
        city: selectedCity,
        months_ahead: monthsAhead,
      });
      setResults(response.data);
    } catch (error) {
      console.error("There was an error predicting the prices!", error);
    }
  };

  return (
    <div className="App">
      <h1>Housing Price Prediction</h1>
      <form onSubmit={handleSubmit}>
        <label>
          State:
          <select value={selectedState} onChange={(e) => setSelectedState(e.target.value)}>
            <option value="">Select a state</option>
            {Object.keys(statesAndCities).map((state) => (
              <option key={state} value={state}>{state}</option>
            ))}
          </select>
        </label>
        <br />
        <label>
          City:
          <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)} disabled={!selectedState}>
            <option value="">Select a city</option>
            {selectedState && statesAndCities[selectedState].map((city) => (
              <option key={city} value={city}>{city}</option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Months Ahead:
          <input type="number" value={monthsAhead} onChange={(e) => setMonthsAhead(e.target.value)} />
        </label>
        <br />
        <button type="submit">Predict</button>
      </form>
      {results && (
        <div>
          <h2>Prediction Results</h2>
          <p>Future Prices: {results.future_prices.join(', ')}</p>
          <p>Model Accuracy: {results.accuracy}</p>
        </div>
      )}
    </div>
  );
}

export default App;
